const htmlnano = require('./index');
const fs = require('fs');
const preset = require('./lib/presets/max').default;

const options = {};

const html = fs.readFileSync('./test.html', 'utf8');

htmlnano
    .process(html, options, preset)
    .then(function (result) {
        console.log(result.html);
    })
    .catch(function (err) {
        console.error(err);
    });
